import React from 'react';
// import axios from 'axios';

// const APP_URL = 'https://167.99.77.218'

class Form extends React.Component {
  state = {
    ages: [1, 2, 3, 4, 5, 6],
    hobies: [
      { id: 1, name: 'Đá bóng'},
      { id: 2, name: 'Đọc sách'},
      { id: 3, name: 'Cầu lông'},
      { id: 4, name: 'Bóng rổ'},
    ],
    form: {
      name: '',
      email: '',
      password: '',
      phone: '',
      note: '',
      age: 2,
      gender: '',
      hobbies: []
    }
  }

  onChange = event => {
    const { name, value, type } = event.target
    // console.log({name,value})
    let newValue = value

    if ( type === 'checkbox') {
      const oldValue = this.state.form.[name]
      const isExist = oldValue.includes(value)
      newValue = isExist ? oldValue.filter(item => item != value) : oldValue.push(value)
      
      if(isExist) {
        newValue = oldValue.filter(item => item != value)
      }
    }
    this.setState({
      ...this.state,
      form: {
        ...this.state.form,
        [name]: newValue
      }
     })
  }

  // componentDidMount() {
  //   axios.get(`${APP_URL}/test-react/get-hobbies`)
  // }

  render() {
    const {
      ages,
      hobies,
      form
    } = this.state

    return(
      <div>
        <table>
          <thead>
            <tr>
              <th>Nội dung</th>
              <th>Giá trị</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>Họ tên</td>
              <td><input type="text" value={ form.name } name="name" onChange={this.onChange}/></td>
            </tr>
            <tr>
              <td>Email</td>
              <td><input type="email" value={ form.email } name="email" onChange={this.onChange}/></td>
            </tr>
            <tr>
              <td>Mật khẩu</td>
              <td><input type="password" name="password" value={form.password} onChange={this.onChange}></input></td>
            </tr>
            <tr>
              <td>Avatar</td>
              <td><input type="file"></input></td>
            </tr>
            <tr>
              <td>SĐT</td>
              <td><input type="text" name="phone" value={form.phone} onChange={this.onChange}></input></td>
            </tr>
            <tr>
              <td>Tuổi</td>
              <td>
                <select name="age" value={form.age} defaultValue={form.age} onChange={this.onChange}>
                  {ages.map((age,index) =>{
                    return <option
                      
                      key={index}
                      value={age}
                      onChange={this.onChange}
                      name="age">
                        {age} tuoi</option> 
                  })}
                  
                </select>
              </td>
            </tr>
            <tr>
              <td>Giới tính</td>
              <td>
                <label><input defaultChecked={form.gender} type="radio" name="gender" value="male" onChange={this.onChange}/>Nam</label>
                <label><input defaultChecked={form.gender} type="radio" name="gender" value="female" onChange={this.onChange}/>Nữ</label>
              </td>
            </tr>
            <tr>
              <td>Sở thích</td>
              <td>
                {
                  hobies.map(hobby => {
                    
                    return <label>
                      <input
                      defaultChecked={form.hobbies.includes(hobby.id)}
                      key={hobby.id}
                      value={hobby.id}
                      type="checkbox"
                      name="hobbies"
                      />
                      {hobby.name}
                    </label>
                  })
                }
              </td>
            </tr>
            <tr>
              <td>Ghi chú</td>
              <td>
                <textarea rows="10" cols="20" name="note" value={form.note} onChange={this.onChange}></textarea>
              </td>
            </tr>
          </tbody>
          <tfoot>
          <tr>
              <td></td>
              <td>
                <input type="submit"></input>
              </td>
            </tr>
          </tfoot>
        </table>
      </div>
    )
  }
}

export default Form
